var PrevMsgTS = context.get('PrevMsgTS') || "";
if (PrevMsgTS == "") {
    PrevMsgTS = new Date(null);
} 

var CurrentMsgTS = Date.now();

var ElapsedTimeMS=CurrentMsgTS - PrevMsgTS;
if (ElapsedTimeMS <= 10000) {
    msg.payload = [];
} else {
    context.set("PrevMsgTS", CurrentMsgTS );
}
//node.warn("PrevMsgTS: " + PrevMsgTS + " ; CurrentTS: " + CurrentMsgTS + "; Elapsed time (s): " + ElapsedTimeMS/1000 );
return msg;

//LETS AGREGATE CLASSES WHEN MULTI OBJECT DETECTION
// SCORE IS THE LOWEST SCORE DETECTED

var newMsg = msg;
var classy="";
newMsg.payload.forEach(function (row) {
    //var example = {};
    newMsg.payload.camera = row.camera;
    //node.warn("Camera:" + row.camera);
    newMsg.payload.timestamp = row.timestamp;
    newMsg.payload.score= row.score;
    if (classy == "")
        classy = row.class ;
     else
        classy = classy+"|"+row.class ; 
})
newMsg.payload.class=classy;
//node.warn("newMsg.payload: " + newMsg.payload.class);

newMsg.payload.query="INSERT INTO VMSAPP.VMSEVENTS(OBJECTTYPE, DETECTION_SCORE, EVENT_TS, EVENT_SRC) values('"+
newMsg.payload.class+"','"+
newMsg.payload.score+"',"+
    "TIMESTAMP_FORMAT('" + newMsg.payload.timestamp.substr(0, newMsg.payload.timestamp.length-1).replace('T',' ')+"', 'YYYY-MM-DD HH24:MI:SS.FF3')"
+",'"+
    newMsg.payload.camera +"')";

node.warn("REQUEST: " + newMsg.payload.query );

return newMsg;

